n=1; a=-1;b=1;step1=(b-a)/n;x=a:step1:b;y=1./(1+100.*x.*x);for i=0:n xc(i+1)=cos((2*i+1)*pi/(2*n+2)); end;yc=1./(1+100.*xc.*xc);step2=(b-a)/100;xt=a:step2:b;wn=polyfit(x,y,n);wnc=polyfit(xc,yc,n);ywn=polyval(wn,xt);ywnc=polyval(wnc,xt);plot(x,y,'ko',xc,yc,'g*',xt,1./(1+100.*xt.*xt),'c-',xt,ywn,'b-',xt,ywnc,'r-');legend('wezly rownoodlegle','wezly Czebyszewa','funkcja interpolowana','wielomian Lagrange-a','wielomian Czebyszewa');